	private final Lexeme lexeme;
	private final Multimap<Form, Word> expected;
	
	public AdjectiveLexemeTest (Word[] pps, Word[] expected) {
		this.lexeme = lexemes.adjective(pps);
		
		ImmutableMultimap.Builder<Form, Word> builder = new ImmutableMultimap.Builder<>();
		
		for (Word word : expected) {
			builder.put(word.getForm(), word);
		}
		
		this.expected = builder.build();
	}

	@Test
	public void test() {
		for (Form form : allForms) {
			Collection<Word> result = this.lexeme.get(form);
			Collection<Word> wanted = this.expected.get(form);
			
			if (result.size() != wanted.size()) {
				fail ("Form: " + form + "\nWanted: " + wanted + "\nGot: " + result + "\n");
			}
			
			for (Word word : wanted) {
				if (!result.contains(word))
					fail ("Form: " + form + "\nWanted: " + wanted + "\nGot: " + result + "\n");
			}
		}
	}

